import React from 'react';
import './Card.css';
import {Link} from 'react-router-dom';

export default function Card ({min, max, name, img, onClose, id}) {
    return (
      <div className="card">
        <div id="closeIcon" className="row">
            <button onClick={onClose} className="btn btn-sm btn-danger">X</button>
        </div>
        <Link to={`/ciudad/${id}`} className='text-decoration-none'>
            <h5 className="card-title text-light hoverable">{name}</h5>
          </Link>
        <div className="card-body">

          <div className="row">
            <div className="col-sm-4 col-md-4 col-lg-4">
              <p>Min</p>
              <p>{min}°</p>
            </div>
            <div className="col-sm-4 col-md-4 col-lg-4">
              <p>Max</p>
              <p>{max}°</p>
            </div>
            <div className="col-sm-4 col-md-4 col-lg-4  view overlay zoom">
              <img className="iconoClima img-fluid" src={"http://openweathermap.org/img/wn/"+img+"@2x.png"} width="80" height="80" alt="" />
            </div>

          </div>
        </div>
      </div>
    );
};
